import React, {Component} from 'react'
import ApiRepository from '../Library/ApiRepositories'

class Menu extends Component{

    render(){
            const {title, url, parent, status, sortby} = '' 
        return (

            <React.Fragment>
            <div className="container">
                <div className="row justify-content-center">
                    <div className="col-md-8">
                        <div className="card">
                            <div className="card-header">Create</div>

                            <div className="card-body">
                                <form onSubmit={event => this.handleFormSubmit(event)}>

                                    <div className="form-group row">
                                        <label htmlFor="title" className="col-md-4 col-form-label text-md-right">Title</label>
                                        <div className="col-md-6"> <input type="title" className="form-control" name="title" value={title} onChange ={event => this.setState({title: event.target.value})} required autoComplete="title" autoFocus /> </div>
                                    </div>

                                    <div className="form-group row">
                                        <label htmlFor="parent" className="col-md-4 col-form-label text-md-right">parent</label>
                                        <div className="col-md-6"> 
                                            <select id="status" className="form-control" onChange ={event => this.setState({status: event.target.value})} required autoComplete="status" autoFocus>
                                            <option value="1">1</option>
                                            <option value="2">2</option>
                                            <option value="2">3</option>
                                            <option value="2">4</option>
                                        </select>
                                        </div>
                                    </div>

                                    <div className="form-group row">
                                        <label htmlFor="url" className="col-md-4 col-form-label text-md-right">Url</label>
                                        <div className="col-md-6"> <input type="url" className="form-control" name="url" value={url} onChange ={event => this.setState({url: event.target.value})} required autoComplete="url" autoFocus /> </div>
                                    </div>

                                    <div className="form-group row">
                                        <label htmlFor="status" className="col-md-4 col-form-label text-md-right">Status</label>
                                        <div className="col-md-6"> 
                                        <select id="status" className="form-control" onChange ={event => this.setState({status: event.target.value})} required autoComplete="status" autoFocus>
                                            <option value="active">Active</option>
                                            <option value="inactive">Inactive</option>
                                        </select>
                                        </div>
                                    </div>

                                    <div className="form-group row">
                                        <label htmlFor="sortby" className="col-md-4 col-form-label text-md-right">Sortby</label>
                                        <div className="col-md-6"> <input type="sortby" className="form-control" name="sortby" value={sortby} onChange ={event => this.setState({sortby: event.target.value})} required autoComplete="sortby" autoFocus /> </div>
                                    </div>



                                    <div className="form-group row mb-0">
                                        <div className="col-md-8 offset-md-4">
                                            <button
                                            type="submit"
                                            className="btn btn-primary" >
                                                Login
                                            </button>

                                                <a className="btn btn-link" href="">
                                                    Forgot Your Password?
                                                </a>

                                        </div>
                                    </div>
                                </form>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
            </React.Fragment>



        )
    }

            // <React.Fragment>
            // <div className="container">
            //     <div className="row justify-content-center">
            //         <div className="col-md-8">
            //             <div className="card">
