import React, {Component} from 'react'
import ApiRepository from '../Library/ApiRepositories'

class Menu extends Component{

    render(){
            const {title, url, parent, status, sortby} = '' 
        return (

            <React.Fragment>
            <div className="container">
                <div className="row justify-content-center">
                    <div className="col-md-8">
                        <div className="card">
                            <div className="card-header">Create</div>

                            <div className="card-body">
                                <form onSubmit={event => this.handleFormSubmit(event)}>

                                    <div className="form-group row">
                                        <label htmlFor="email" className="col-md-4 col-form-label text-md-right">E-Mail Address</label>
                                        <div className="col-md-6"> <input type="email" className="form-control" name="email" value={email} onChange ={event => this.setState({email: event.target.value})} required autoComplete="email" autoFocus /> </div>
                                    </div>

                                    <div className="form-group row">
                                        <label htmlFor="email" className="col-md-4 col-form-label text-md-right">E-Mail Address</label>
                                        <div className="col-md-6"> <input type="email" className="form-control" name="email" value={email} onChange ={event => this.setState({email: event.target.value})} required autoComplete="email" autoFocus /> </div>
                                    </div>

                                    <div className="form-group row">
                                        <label htmlFor="email" className="col-md-4 col-form-label text-md-right">E-Mail Address</label>
                                        <div className="col-md-6"> <input type="email" className="form-control" name="email" value={email} onChange ={event => this.setState({email: event.target.value})} required autoComplete="email" autoFocus /> </div>
                                    </div>

                                    <div className="form-group row">
                                        <label htmlFor="email" className="col-md-4 col-form-label text-md-right">E-Mail Address</label>
                                        <div className="col-md-6"> <input type="email" className="form-control" name="email" value={email} onChange ={event => this.setState({email: event.target.value})} required autoComplete="email" autoFocus /> </div>
                                    </div>

                                    <div className="form-group row">
                                        <label htmlFor="email" className="col-md-4 col-form-label text-md-right">E-Mail Address</label>
                                        <div className="col-md-6"> <input type="email" className="form-control" name="email" value={email} onChange ={event => this.setState({email: event.target.value})} required autoComplete="email" autoFocus /> </div>
                                    </div>

                                    <div className="form-group row">
                                        <div className="col-md-6 offset-md-4">
                                            <div className="form-check">
                                                <input
                                                className="form-check-input"
                                                type="checkbox"
                                                name="remember"
                                                checked ={isChecked}
                                                onChange={() => this.handleChecked()} 
                                                id="remember" />

                                                <label className="form-check-label" htmlFor="remember">
                                                    Remember Me
                                                </label>
                                            </div>
                                        </div>
                                    </div>

                                    <div className="form-group row mb-0">
                                        <div className="col-md-8 offset-md-4">
                                            <button
                                            type="submit"
                                            className="btn btn-primary" >
                                                Login
                                            </button>

                                                <a className="btn btn-link" href="">
                                                    Forgot Your Password?
                                                </a>

                                        </div>
                                    </div>
                                </form>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
            </React.Fragment>



        )
    }

