import React from 'react';
import ReactDOM from 'react-dom';
import Header from './Components/Header';
import Home from './Pages/Home';
import Footer from './Components/Footer';
import ApiRepository from './Library/ApiRepositories'

class Index extends React.Component {

	constructor(props){
		super(props);
		this.header = ApiRepository.GetMenu();
		this.state = {
			header: this.header,
			footer: null,
			body: null
		}
		this.getRequiredData()
	}
	async getRequiredData() {
        this.header = await ApiRepository.GetMenu();
        this.footer = await ApiRepository.GetMenu();
		this.body = await ApiRepository.GetMenu();
		// console.clear()
		console.log('menu')
		console.log(this.header)
		console.log('state before')
		console.log(this.state)
		this.setState({
			header: this.header
		})
		console.log('state after')
		console.log(this.state)



	 }

	render(props){
        return(
			<div>
				<Header header = {this.state.header} />
				<Home/>
				<Footer/>
			</div>
		);
	}
}

// response = await ApiRepository.GetMenu();
if (document.getElementById('root')) {
    ReactDOM.render(<Index />, document.getElementById('root'));
}

