import React, {Component} from 'react'
import ApiRepository from '../Library/ApiRepositories'

class Menu extends Component{

    render(){
        return (
            <div class="container mt-5 mb-5">
                <label><h2>Get Menu</h2></label><br />
                <button className="btn btn-primary" onClick={event => this.GetMenu(event)}>Get All Menu Data</button><br />
                <div className="form-group row"><label>id : </label>
                    <input type="text" className="form-control" name="url" value='' required autoComplete="url" autoFocus />  
                    <button className="btn btn-primary" onClick={event => this.GetMenu(event , 2)}>Get Single Menu Data</button><br />
                </div>
                <div id="get-response"></div><br />

             <label><h2>Create Menu</h2></label>
             <div class="md-6 mb-5>">
                <div className="form-group "> <label> title: </label>  <input type="text" className="form-control" name="title" value=''   required autoComplete="title" autoFocus /></div>
                <div className="form-group row"> <label> url: </label>  <input type="text" className="form-control" name="url" value=''   required autoComplete="url" autoFocus /></div>
                <div className="form-group row"> <label> parent: </label>  <input type="text" className="form-control" name="parent" value=''   required autoComplete="parent" autoFocus /></div>
                <div className="form-group row"> <label> status: </label>  <input type="text" className="form-control" name="status" value=''   required autoComplete="status" autoFocus /></div>
                <div className="form-group row"> <label> sortby: </label>  <input type="text" className="form-control" name="sortby" value=''   required autoComplete="sortby" autoFocus /></div>
                <button onClick={event => this.CreateMenu(event)}>Create Menu</button><br />
                <div id="create-response"></div>   
                </div>




            </div>
        )
    }

    async GetMenu(event, id) {
        event.preventDefault();
        const response = await ApiRepository.GetMenu(id);
        if(response){
            console.clear()
            console.log(JSON.stringify(response))
            document.getElementById('data').innerHTML = JSON.stringify(response)
        }
        else{
            alert('Something Went Wrong');
        }
    }

    // async Create



}

export default Menu
