import React from 'react';
import ReactDOM from 'react-dom';
import Header from './Components/Header';
import Home from './Pages/Home';
import Footer from './Components/Footer';
import ApiRepository from './Library/ApiRepositories'
import Menu from './Pages/Menu';

class Index extends React.Component {

	constructor(props){
		super(props);
		this.state = {
			header: null,
			footer: null,
			body: null
		}    		
	}

	componentWillMount() {
		this.getRequiredData()
	}

	async getRequiredData() {
        const header = await ApiRepository.GetMenu();
        const footer = await ApiRepository.GetMenu(4);
		const slider = await ApiRepository.GetSlider();
		const body = {
			slider: slider.json()
		}
		console.clear()
		console.log('data')
		console.log(header)
		console.log(body)
		console.log(footer	)
		this.setState({
			header: header, 
			body: body, 
			footer: footer
		})
		return {header: header, body: body, footer: footer}                              
	 }

	render(props){
        return(
			<div>
				<Header header = {this.state.header} />
				<Home  body = {this.state.body} />
				<Footer/>
			</div>
		);
	}
}
Index.defaultProps = { age: 'unknown' };

