import React from 'react';
import ReactDOM from 'react-dom';
import Header from './Components/Header';
import Home from './Pages/Home';
import Footer from './Components/Footer';
import ApiRepository from './Library/ApiRepositories'
import Menu from './Pages/Menu';

class Index extends React.Component {

	constructor(props){
		super(props);
		this.property = this.getRequiredData();
		this.state = {
			header: this.property.header,
			footer: null,
			body: null
		}    		
	}

	componentWillMount() {
		let mode;
		if (this.props.age > 70) {
		  mode = 'old';
		} else if (this.props.age < 18) {
		  mode = 'young';
		} else {
		  mode = 'middle';
		}
		this.setState({ body: mode });
	}

	async getRequiredData() {
        const header = await ApiRepository.GetMenu();
        const footer = await ApiRepository.GetMenu(1);
		const body = await ApiRepository.GetMenu(2);
		this.setState({
			header: header, 
			body: body, 
			footer: footer
		})
		return {header: header, body: body, footer: footer}                              
	 }

	render(props){
		const property = this.getRequiredData();
		console.log('property')
		console.log(property)
		console.log('state')
		console.log(this.state)
        return(
			<div>
				<Header header = {property.header} />
				<Home/>
				<Footer/>
			</div>
		);
	}
}

