import React from 'react';
import ReactDOM from 'react-dom';
import Header from './Components/Header';
import Home from './Pages/Home';
import Footer from './Components/Footer';
import ApiRepository from './Library/ApiRepositories'

class Index extends React.Component {

	constructor(props){
		super(props);
		this.property = this.getRequiredData();
		console.log('this property header')
		console.log(this.property.header)
		this.state = {
			header: this.property.header,
			footer: null,
			body: null
		}
		// this.getRequiredData()      		
	}
	async getRequiredData() {
        this.header = await ApiRepository.GetMenu();
        this.footer = await ApiRepository.GetMenu();
		this.body = await ApiRepository.GetMenu();
