		console.log(this.state)
		this.setState({
			header: this.header
		})
		console.log('state after')
		console.log(this.state)
		const response =  {
			header: this.header,
		}
		return {header: this.header, body: this.body, footer: this.footer}



	 }

	render(props){
		const property = this.getRequiredData();
        return(
			<div>
				<Header header = {property.header} />
				<Home/>
				<Footer/>
			</div>
		);
	}
}

