import React, {Component} from 'react'
import  {Button}  from 'react-bootstrap';
// import Icon from 'react-native-vector-icons/FontAwesome';
import { Link } from 'react-router-dom'
import ApiRepository from '../Library/ApiRepositories'
const axios = require('axios').default; 

class Test extends Component{
    constructor(props) {
        super(props);
        this.state = {value: ''};
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
      }

      handleChange(event) {    this.setState({value: event.target.value});  }
      handleSubmit(event) {
        alert('A name was submitted: ' + this.state.value);
        event.preventDefault();
      }

      async GetBlogData(event) {
        event.preventDefault();
        const postRegisterData = {
            name: this.state.name,
            email: this.state.email,
            password: this.state.password,
            password_confirmation: this.state.password
        }
        const response = await ApiRepository.doUserRegistration(postRegisterData);
        if(response){
            const postLoginData = {
                username: this.state.email,
                password: this.state.password,
                grant_type: 'password',
                client_id: 2,
                client_secret: 'h4Rszv34VV9o0BHKhowVSLEg5kfFIEvNlJoALpHc'
            }
            const response = await ApiRepository.doUserLogin(postLoginData);
            ApiRepository.handleLoginSucces(response);
            window.location.reload(false);
        }
        else{
            alert('Something Went Wrong');
        }
    }
    render(){
        return (
            <React.Fragment>
                <h1>Home</h1><br />
                <Link to='/root'>Root</Link><br />
                <Button onClick={shoot}>Take the Shot!</Button><br />
                <Button onClick={event => this.GetBlogData(event)}>Get Blog Data</Button>

            </React.Fragment>
        )
    }
}

function shoot() {
    alert("Great Shot!");
}










